mapper.select:
select

from doc_order_big


    AND order_id = #{orderId,jdbcType=VARCHAR}


    AND ebp_code = #{ebpCode,jdbcType=VARCHAR}


    AND ebc_code = #{ebcCode,jdbcType=VARCHAR}


    AND tpl = #{tpl,jdbcType=VARCHAR}


    AND delivery_code = #{deliveryCode,jdbcType=VARCHAR}


    AND ciqb_code = #{ciqbCode,jdbcType=VARCHAR}


    AND busi_mode = #{busiMode,jdbcType=INTEGER}


    AND order_type = #{orderType,jdbcType=INTEGER}


    AND customs_type = #{customsType,jdbcType=INTEGER}


    AND customs_code = #{customsCode,jdbcType=VARCHAR}


    AND pay_date  >=  #{payDateStart,jdbcType=TIMESTAMP}


    AND pay_date  <  #{payDateEnd,jdbcType=TIMESTAMP}


    AND from_eplat = #{fromEplat,jdbcType=INTEGER}


    AND owner_flag = #{ownerFlag,jdbcType=INTEGER}


    AND create_time  >=  #{createTimeStart,jdbcType=TIMESTAMP}


    AND create_time  <  #{createTimeEnd,jdbcType=TIMESTAMP}


    AND vmi_flag = #{vmiFlag,jdbcType=INTEGER}


    AND is_store_strategy = #{isStoreStrategy,jdbcType=INTEGER}


    AND order_date  >=  #{orderDateStart,jdbcType=TIMESTAMP}


    AND order_date  <  #{orderDateEnd,jdbcType=TIMESTAMP}


order by create_time desc

select

from doc_order_big
where ebc_code = #{electricCode,jdbcType=VARCHAR}
and ebp_code =  #{cbepcomCode,jdbcType=VARCHAR}
and busi_mode = #{busiMode,jdbcType=VARCHAR}
and order_id = #{orderId,jdbcType=VARCHAR}

select from doc_order_big
and id in
#{id}
