mapper.select:
select

from doc_order_pub


    AND status = #{status,jdbcType=INTEGER}





    AND order_no like concat('%',#{orderNo},'%')


    AND code like concat('%',#{code},'%')


    AND store_code like concat('%',#{storeCode},'%')


    AND platform_code like concat('%',#{platformCode},'%')


    AND create_time  >=  #{createTimeStart,jdbcType=TIMESTAMP}


    AND create_time  <  #{createTimeEnd,jdbcType=TIMESTAMP}


order by create_time desc

select
from doc_order_pub where id in
#{id,jdbcType=BIGINT}

select

from doc_order_pub
where store_code = #{storeCode,jdbcType=VARCHAR} and order_no = #{orderNo,jdbcType=VARCHAR}
